# 2. Generate Embeddings
for poi_id, text in selected_poi_texts.items():
    # Generate the 768-dim vector
    # normalize_embeddings=True ensures cosine similarity is equivalent to dot product
    embedding = model.encode(text, normalize_embeddings=True)
    
    # Store with ID as string to match your JSON loading logic
    poi_embeddings[str(poi_id)] = embedding.tolist()

print("Encoding complete. Saving to poi_embeddings.txt...")

# 3. Save to file in a compact, MRS-ready format
with open('poi_embeddings.txt', 'w', encoding='utf-8') as f:
    f.write("{\n")
    items = list(poi_embeddings.items())
    for i, (poi_id, embedding) in enumerate(items):
        # Format: "ID": [0.1234, 0.5678, ...]
        # Using 4 decimal places saves space while maintaining precision
        embedding_str = "[" + ", ".join([f"{x:.4f}" for x in embedding]) + "]"
        
        comma = "," if i < len(items) - 1 else ""
        f.write(f'  "{poi_id}": {embedding_str}{comma}\n')
    f.write("}")

print(f"✓ Success! Generated 768-dim embeddings for {len(poi_embeddings)} POIs.")
